import {Form, Icon, Input, Button, Radio} from 'antd';
import React from "react";
import {Row, Col} from 'antd';

function hasErrors(fieldsError) {
    return Object.keys(fieldsError).some(field => fieldsError[field]);
}

const FORM_LOGIN = 1
const FORM_REGISTER = 2

class RegisterForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            typeForm : FORM_LOGIN
        }
    }


    componentDidMount() {
        this.props.form.validateFields();
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    handleFormLayoutChange = e => {
        this.setState({
            typeForm 
        })
    }

    render() {
        const {getFieldDecorator, getFieldsError, getFieldError, isFieldTouched} = this.props.form;

        // Only show error after a field is touched.
        const usernameError = isFieldTouched('username') && getFieldError('username');
        const passwordError = isFieldTouched('password') && getFieldError('password');
        return (
            <Row>
                <Col span={8} offset={8}>
                    <Form layout="vertical" onSubmit={this.handleSubmit}>

                        <Radio.Group defaultValue="horizontal" onChange={this.handleFormLayoutChange}>
                            <Radio.Button value="signin">Signin</Radio.Button>
                            <Radio.Button value="register">Register</Radio.Button>
                        </Radio.Group>


                        <div>
                            <Form.Item validateStatus={usernameError ? 'error' : ''} help={usernameError || ''}>
                                {getFieldDecorator('username', {
                                    rules: [{required: true, message: 'Please input your username!'}],
                                })(
                                    <Input
                                        prefix={<Icon type="user" style={{color: 'rgba(0,0,0,.25)'}}/>}
                                        placeholder="Username"
                                    />,
                                )}
                            </Form.Item>
                            <Form.Item validateStatus={passwordError ? 'error' : ''} help={passwordError || ''}>
                                {getFieldDecorator('password', {
                                    rules: [{required: true, message: 'Please input your Password!'}],
                                })(
                                    <Input
                                        prefix={<Icon type="lock" style={{color: 'rgba(0,0,0,.25)'}}/>}
                                        type="password"
                                        placeholder="Password"
                                    />,
                                )}
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" disabled={hasErrors(getFieldsError())}>
                                    Log in
                                </Button>
                            </Form.Item>
                        </div>
                    </Form>
                </Col>
            </Row>
        );
    }
}

RegisterForm = Form.create()(RegisterForm);
export {RegisterForm as default};  // imported as SomeComponent
